var demoProfile = function(app, router) {

    app.get('/demo/profile', function (req, res) {
        res.render('steamize', {view: {'steamId' : "demo/profile", 'page': 'games'}});
    });

    app.get('/demo/profile/:app_id', function (req, res) {
        res.render('steamize', {view: {'steamId' : "demo/profile", 'page': 'gameSummary', 'appId': req.params.app_id}});
    });

    router.route('/id/demo/profile')
        .get(function (req, res) {
            res.json(demoProfile.ProfileData);
        });

    router.route('/id/demo/profile/games')
        .get(function (req,res) {
            res.json(demoProfile.GameData);
        });

    router.route('/id/demo/profile/reviews/:app_id')
        .get(function (req,res) {
            res.json(demoProfile.ReviewData);
        });
};

